mapper.select:
select tz.pzh,mxdh,tz.spkh,spda.ksmc,cmzbh,ysda.ysmc,tz.bxbh,bxda.bxmc,tz.ysbh,tz.yslsh,jldw,pic.img,tz.spbh,convert(decimal(30,2),jj) jj,convert(decimal(30,2),lsj)lsj,convert(decimal(30,2),dpj)dpj,convert(decimal(30,2),zkl)zkl,convert(decimal(30,0),tz.sl)sl,spmx.cmbh,spmx.cmdmlwz cm,convert(decimal(30,2),hjjze)hjjze,spmx.tmbh from yw_cg_cgrkdtz tz left join da_sp_spda spda on tz.spkh=spda.spkh left join da_sp_ysda ysda on tz.yslsh=ysda.yslsh left join da_sp_bxda bxda on tz.bxbh=bxda.bxbh left join da_sp_spda_mx spmx on tz.spbh=spmx.spbh LEFT JOIN ( SELECT p.spkh , p.default_disp img FROM dbo.da_sp_pic p , ( SELECT spkh , MIN(pxxh) pxxh FROM dbo.da_sp_pic GROUP BY spkh ) p1 WHERE p.spkh = p1.spkh AND p.pxxh = p1.pxxh ) pic ON spda.spkh = pic.spkh
and tz.pzh = #{pzh}
order by tz.spkh,tz.ysbh,tz.bxbh,convert(int,substring(spmx.cmdmlwz,3,2))

select tz.pzh,tz.spkh+tz.yslsh ysspkh,mxdh,tz.spkh,spda.ksmc,cmzbh,ysda.ysmc,tz.bxbh,bxda.bxmc,tz.ysbh,tz.yslsh,jldw,pic.img,tz.spbh,convert(decimal(30,2),jj) jj,convert(decimal(30,2),lsj)lsj,convert(decimal(30,2),dpj)dpj,convert(decimal(30,2),zkl)zkl,convert(decimal(30,0),tz.sl)sl,spmx.cmbh,spmx.cmdmlwz cm,convert(decimal(30,2),hjjze)hjjze,spmx.tmbh from yw_cg_cgrkdtz tz left join da_sp_spda spda on tz.spkh=spda.spkh left join da_sp_ysda ysda on tz.yslsh=ysda.yslsh left join da_sp_bxda bxda on tz.bxbh=bxda.bxbh left join da_sp_spda_mx spmx on tz.spbh=spmx.spbh LEFT JOIN ( SELECT p.spkh , p.default_disp img FROM dbo.da_sp_pic p , ( SELECT spkh , MIN(pxxh) pxxh FROM dbo.da_sp_pic GROUP BY spkh ) p1 WHERE p.spkh = p1.spkh AND p.pxxh = p1.pxxh ) pic ON spda.spkh = pic.spkh
and tz.pzh = #{pzh}
order by tz.spkh+tz.yslsh
